sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var answer = {};

    // Get The Number from the path parameter, otherwise 404
    var number = request.pathParams.number;
    if (!number) {
        response.setError(new sn_ws_err.NotFoundError('Number not provided or Invalid Number'));
        response.setStatus(404);
        return;
    }

    var championsGR = new GlideRecord('x_886019_champions_champions');

    // Check if the record exists and delete it
    if (championsGR.get('number', number)) {
        var championObj = {
            "name": championsGR.getValue('name'),
            "description": championsGR.getValue('description'),
            "role": championsGR.getValue('role'),
            "active": championsGR.getValue('active'),
            'title': championsGR.getValue('title'),
            "release_date": championsGR.getValue('release_date'),
            'region': championsGR.getValue('region')
        };

        // Delete the record
        var deletionResult = championsGR.deleteRecord();

        if (deletionResult) {
            // If deletion is successful
            answer.result = "Success";
            answer.message = "Champion '" + championObj.name + "' was successfully deleted.";
            response.setStatus(200); // OK Status
        } else {
            // If deletion failed
            answer.result = "Error";
            answer.message = "Failed to delete the champion.";
            response.setStatus(500); // Internal Server Error
        }
    } else {
        // If no record is found
        answer.result = "Error";
        answer.message = "No champion found with the provided number.";
        response.setStatus(404); // Not Found
    }

    response.setBody(answer);

})(request, response);
