sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var answer = [];

    var number = request.pathParams.number.toString();

    // Declare variables needed to update
    var requestBody = request.body.data;
    var name = requestBody.name;
    var description = requestBody.description;
    var role = requestBody.role;
    var active = requestBody.active;
    var title = requestBody.title;
    var release_date = requestBody.release_date;
    var region = requestBody.region;

    // Generate 404 if Champion Number not provided.
    if (!number) {
        response.setError(new sn_ws_err.NotFoundError('Number not provided'));
        response.setStatus(404);
        return;
    }

    // Query Champions table for this Number
    var championsGR = new GlideRecord('x_886019_champions_champions');

    if (championsGR.get('number', number)) {

		if (name) {championsGR.name = name;}
		if (name) {championsGR.name = name;}
		if (description) {championsGR.description = description;}
		if (role) {championsGR.role = role;}
		if (active) {championsGR.active = active;}
		if (title) {championsGR.title = title;}
		if (release_date) {championsGR.release_date = release_date;}
		if (region) {championsGR.region = region;}

		championsGR.update();

    } else {
        response.setError(new sn_ws_err.NotFoundError('Champion with this Number not found'));
        response.setStatus(404);
        return;
    }

    // Generate the response body with all champions created
    response.setStatus(200);

	// Create a JSON obj with details of updated record
	var championObj = {
            "name": championsGR.getValue('name'),
            "description": championsGR.getValue('description'),
            "role": championsGR.getValue('role'),
            "active": championsGR.getValue('active'),
            'title': championsGR.getValue('title'),
            "release_date": championsGR.getValue('release_date'),
            'region': championsGR.getValue('region')
        };
	answer.push(championObj);

	response.setBody(answer);

})(request, response);
