sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	var answer = [];

	var name;
    var description;
    var role;
    var active;
    var title;
    var release_date;
    var region;

	// Get the request body
	var requestBody = request.body;

	// Convert JSON obj to JS string
	var requestString = requestBody.dataString;

	//Conver JS string to arr
	var result = JSON.parse(requestString);

	// For every champion in array, create a new record
	try {
		for (i in result.champions) {
			name = result.champions[i].name;
			description = result.champions[i].description;
			role = result.champions[i].role;
			active = result.champions[i].active;
			title = result.champions[i].title;
			release_date = result.champions[i].release_date;
			region = result.champions[i].region;

			var championsGR = new GlideRecord('x_886019_champions_champions');
			championsGR.initialize();
			championsGR.name = name;
			championsGR.description = description;
			championsGR.role = role;
			championsGR.active = active;
			championsGR.title = title;
			championsGR.release_date = release_date;
			championsGR.region = region;
			championsGR.insert();

			var championObj = {
				"sys_id": championsGR.sys_id,
				"number": championsGR.number
			};
			answer.push(championObj);
		}
		response.setStatus(201);
	} catch(error) {
		var serviceError = new sn_ws_err.ServiceError();
		serviceError.setStatus(500);
		serviceError.setMessage('Service Error');
		serviceError.setDetail('The server could not process the request');
		response.setError(serviceError);
	}

	// Generate the response body with all champions created
	response.setBody(answer);

})(request, response);
